mapper.select:
select 

from u_wxuser
where tabTime > #{downTime} and areaCode =#{companyCode}

select 

from u_wxuser
where openid = #{openid,jdbcType=INTEGER} 

   and areaCode = #{areacode,jdbcType=VARCHAR}

limit 0,1

select 
u.*,d.inviterOpenid
from u_wxuser u,u_preapplygoupdetail d
where u.openid = d.openid and u.areaCode = d.areaCode 

   and d.syncid = #{syncid,jdbcType=VARCHAR}


   and d.areaCode = #{areacode,jdbcType=VARCHAR}

select  * from u_wxuser where 1=1 

   and uid = #{uid}


   and areaCode = #{areacode,jdbcType=VARCHAR}

limit 0,1

mapper.update:
update u_wxuser
set uid = null
where id = #{id,jdbcType=INTEGER}
