mapper.insert:

insert into u_work_time (wordId, depID, YT,
workStartTime, workEndTime,
recordTime
)
values (#{wordid,jdbcType=INTEGER},
#{depid,jdbcType=INTEGER},
#{yt,jdbcType=VARCHAR},
#{workstarttime,jdbcType=TIME}, #{workendtime,jdbcType=TIME},
#{recordtime,jdbcType=TIMESTAMP}
)

mapper.select:
SELECT * FROM u_department dep, u_work_time wt WHERE dep.id=wt.depID

	AND dep.companyNum=#{companyNum}


	AND wt.depID=#{depID}


	AND LEFT(wt.YT,4)=#{year}

ORDER BY dep.id ,wt.YT
LIMIT
${(num-1)*recordNum},${recordNum}

SELECT COUNT(0) FROM u_department dep, u_work_time wt WHERE
dep.id=wt.depID

	AND dep.companyNum=#{companyNum}


	AND wt.depID=#{depID}


	AND LEFT(wt.YT,4)=#{year}

SELECT * FROM u_department dep, u_work_time wt WHERE dep.id=wt.depID

	AND dep.companyNum=#{companyNum}


	AND wt.depID=#{depID}


	AND wt.YT=#{date}

ORDER BY dep.id ,wt.YT
